import React from "react";
import { useTranslation } from "react-i18next";
import { ButtonGroup } from "react-bootstrap";
import "./AppBar.scss";
import Avatar from "react-avatar";

const AppBar = () => {
  const { t, i18n } = useTranslation();

  const name = "Nguyễn Hải Đăng"

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  return (
    <div className="navbar-app">
      <h1>Kanban</h1>
      <div className="group-info">
        <Avatar name={name} round={true} size="36" />
        <div className="info">
          <h1>{t("text.helloFriend", { name: nameÏ })}</h1>
        </div>
        <ButtonGroup aria-label="Basic example">
          <button
            onClick={() => {
              changeLanguage("vn");
            }}
            style={{
              backgroundColor: `${
                i18n.language === "vn" ? "#957DAD" : "#ffffff"
              }`,
              color: `${i18n.language === "en" ? "#957DAD" : "#ffffff"}`,
            }}
            className="vn-btn"
          >
            {t("text.vn")}
          </button>
          <button
            onClick={() => {
              changeLanguage("en");
            }}
            style={{
              backgroundColor: `${
                i18n.language === "en" ? "#957DAD" : "#ffffff"
              }`,
              color: `${i18n.language === "en" ? "#ffffff" : "#957DAD"}`,
            }}
            className="en-btn"
          >
            {t("text.en")}
          </button>
        </ButtonGroup>
      </div>
    </div>
  );
};

export default React.memo(AppBar);
